mapper.select:
select * from notice
order by notice_date desc
limit #{start},#{length}

mapper.update:
update notice
set notice_id = #{notice_id}, notice_title = #{notice_title},
	notice_contents = #{notice_contents}, notice_type = #{notice_type}
where notice_idx = #{notice_idx}

mapper.select:
select * from notice
where notice_type = #{notice_type}
order by notice_date desc
limit #{start}, #{length}
