mapper.select:
SELECT @rownum:=@rownum-1 as qna_idx,qna_type,qna_title,qna_question,
	qna_answer,qna_secret,qna_member,qna_product,qna_date
from qna AS q,
	(SELECT @rownum:=(SELECT COUNT(*) 
	FROM qna WHERE qna_product = #{qna_product})-#{start}+1) tmp
where qna_product = #{qna_product}
order by q.qna_idx desc
limit #{start}, #{length}

select count(*) 
from qna
where qna_product = #{qna_product}
